import * as React from 'react';
import * as renderer from 'react-test-renderer';
import userEvent from '@testing-library/user-event';
import { render } from '@testing-library/react';
import UploadingImage from './UploadingImage';

describe('Testing component with snapshot', () => {
  it('Testing component with required props', () => {
    const tree = renderer
      .create(
        <UploadingImage
          textInputLetter="A"
          textInputId="1"
          imageBase64="data:image/jpeg;base64,"
        />,
      )
      .toJSON();
    expect(tree).toMatchSnapshot();
  });
});

it('should run a function when clicked', () => {
  const mockFunction = jest.fn();
  const { getByTestId } = render(
    <UploadingImage
      textInputLetter="A"
      textInputId="1"
      handleVerticalThreeDotsClick={mockFunction}
      imageBase64="data:image/jpeg;base64,"
    />,
  );
  const button = getByTestId('VerticalThreeDots');
  userEvent.click(button);
  expect(mockFunction).toHaveBeenCalled();
});

